mapper.select:

select dr.*, d.id as d_id, d.code as d_code, r.id as r_id, r.name as r_name from t_device_resource dr, t_device d, t_resource r
where dr.customer_id = #{customerId} and dr.device_id = d.id and dr.resource_id = r.id


    and dr.check_status = #{deviceResource.checkStatus}


    and dr.status = #{deviceResource.status}

order by dr.create_time desc
limit #{pager.beginIndex}, #{pager.pageSize}


select count(1) from t_device_resource where customer_id = #{customerId}


    and check_status = #{deviceResource.checkStatus}


    and status = #{deviceResource.status}
